content:
  #markdownVideo {
    /* 1. 设置宽度为 100% 以适应父容器 */
    width: 100%; 

    /* 2. 移除高度设置，让浏览器根据视频的原始比例自动计算高度 */
    height: auto; 

    /* 3. 添加圆角美化 */
    border-radius: 15px; 

    /* 4. 确保视频画面也被圆角裁剪 */
    overflow: hidden; 

    /* 可选美化：添加一个柔和的阴影 */
    box-shadow: 0 6px 12px rgba(0, 0, 0, 0.25);
  }

&lt;video
id=“markdownVideo”
src=“/videos/your-video-filename.mp4”
autoplay=“true”
controls=“controls”
muted
poster=“/images/placeholder-image.jpg”
